<?phpnamespace Nemundo\App\ModelDesigner\Site;use Nemundo\App\ModelDesigner\Page\ProjectNewPage;use Nemundo\Web\Site\AbstractSite;class ProjectNewSite extends AbstractSite{    /**     * @var ProjectNewSite     */    public static $site;    protected function loadSite()    {        $this->title = 'New Project';        $this->url = 'project-new';        $this->menuActive = false;        ProjectNewSite::$site = $this;    }    public function loadContent()    {        (new ProjectNewPage())->render();    }}