<?phpnamespace Nemundo\App\ModelDesigner\Site;use Nemundo\App\ModelDesigner\Page\ProjectPage;use Nemundo\App\ModelDesigner\Site\App\AppDeleteSite;use Nemundo\App\ModelDesigner\Site\App\AppEditSite;use Nemundo\App\ModelDesigner\Site\App\AppNewSite;use Nemundo\App\ModelDesigner\Site\App\AppUndoDeleteSite;use Nemundo\Web\Site\AbstractSite;class ProjectSite extends AbstractSite{    /**     * @var ProjectSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Project';        $this->url = 'project';        $this->menuActive = false;        new AppNewSite($this);        new AppEditSite($this);        new AppDeleteSite($this);        new AppUndoDeleteSite($this);        ProjectSite::$site = $this;    }    public function loadContent()    {        ModelDesignerSite::$site->showMenuAsActive = true;        (new ProjectPage())->render();    }}