<?phpnamespace Nemundo\App\ModelDesigner\Template;use Nemundo\Admin\Com\Tab\AdminTab;use Nemundo\App\ClassDesigner\Site\ClassDesignerSite;use Nemundo\App\ModelDesigner\Parameter\AppParameter;use Nemundo\App\ModelDesigner\Parameter\ProjectParameter;use Nemundo\App\ModelDesigner\Site\AppSite;use Nemundo\App\ModelDesigner\Site\ModelDesignerSite;use Nemundo\App\ModelDesigner\Site\Project\DefaultProjectSite;use Nemundo\App\ModelDesigner\Site\ProjectNewSite;use Nemundo\Com\Package\PackageTrait;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Package\Jquery\Package\JqueryPackage;class ModelDesignerTemplate extends AbstractTemplateDocument{    use PackageTrait;    protected function loadContainer()    {        parent::loadContainer();        $tab = new AdminTab($this);        $appParameter = new AppParameter();        $site = clone(ModelDesignerSite::$site);        if ($appParameter->hasValue()) {            $site = clone(AppSite::$site);        }        $site->title = ModelDesignerSite::$site->title;        $site->addParameter(new ProjectParameter());        $site->addParameter(new AppParameter());        $tab->addSite($site);        $site = clone(ProjectNewSite::$site);        $site->addParameter(new ProjectParameter());        $site->addParameter(new AppParameter());        $tab->addSite($site);        $site = clone(ClassDesignerSite::$site);        $site->addParameter(new ProjectParameter());        $site->addParameter(new AppParameter());        $tab->addSite($site);        $site = clone(DefaultProjectSite::$site);        $tab->addSite($site);    }    public function getContent()    {        $this->addPackage(new JqueryPackage());        return parent::getContent();    }}