<?phpnamespace Nemundo\App\ModelDesigner\Type\Form;use Nemundo\Admin\Com\ListBox\AdminTextBox;use Nemundo\App\ModelDesigner\Type\TextModelDesignerType;class TextTypeFormPart extends AbstractModelDesignerTypeFormPart{    /**     * @var TextModelDesignerType     */    public $type;    /**     * @var AdminTextBox     */    private $length;    protected function loadContainer()    {        parent::loadContainer();        $this->length = new AdminTextBox($this);        $this->length->label = 'Length';    }    public function getContent()    {        $this->length->value = $this->type->length;        return parent::getContent();    }    public function getJson()    {        $this->type->length = $this->length->getValue();    }}