<?phpnamespace Nemundo\App\ModelDesigner\Type\Form;use Nemundo\App\ModelDesigner\Type\VirtualExternalModelDesignerType;use Nemundo\Com\Container\LibraryTrait;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;use Nemundo\Web\WebConfig;class VirtualExternalTypeFormPart extends ExternalTypeFormPart{    use LibraryTrait;    /**     * @var VirtualExternalModelDesignerType     */    public $type;    /**     * @var BootstrapListBox     */    private $modelType;    protected function loadContainer()    {        parent::loadContainer();        $this->addJsUrl(WebConfig::$webUrl . '../lib/framework/package/admin/js/model_designer.js');        $this->modelType = new BootstrapListBox($this);        $this->modelType->emptyValueAsDefault = false;        $this->modelType->label = 'External Type';        $this->modelType->inputId = 'external_type';    }    public function getJson()    {        parent::getJson();        $this->type->externalFieldName = $this->modelType->getValue();    }}