<?phpnamespace Nemundo\App\MySql\Page;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\App\MySql\Template\MySqlTemplate;use Nemundo\Db\Provider\MySql\User\MySqlUserReader;class UserPage extends MySqlTemplate{    public function getContent()    {        $table = new AdminBootstrapTable($this);        $header=new AdminTableHeader($table);        $header->addText('User');        $header->addText('Host');        $reader = new MySqlUserReader();        foreach ($reader->getData() as $userItem) {            $row = new AdminTableRow($table);            $row->addText($userItem->user);            $row->addText($userItem->host);        }        return parent::getContent();    }}