<?phpnamespace Nemundo\App\Scheduler\Com\Form;use Nemundo\Admin\Com\Form\AbstractAdminForm;use Nemundo\App\Application\Com\ListBox\ApplicationListBox;use Nemundo\App\Scheduler\Builder\JobBuilder;use Nemundo\App\Scheduler\Data\Job\Job;use Nemundo\App\Scheduler\Status\PlannedSchedulerStatus;use Nemundo\App\Script\Com\ListBox\ScriptListBox;class JobForm extends AbstractAdminForm{    public $applicationId;    /**     * @var ApplicationListBox     */    private $application;    /**     * @var ScriptListBox     */    private $script;    public function getContent()    {        if ($this->applicationId == null) {            $this->application = new ApplicationListBox($this);            if ($this->application->hasValue()) {                $this->applicationId = $this->application->getValue();            }        }        $this->script = new ScriptListBox($this);        $this->script->applicationId = $this->applicationId;        return parent::getContent();    }    protected function onSubmit()    {        (new JobBuilder())->buildJob($this->script->getValue());        /*$data = new Job();        $data->scriptId = $this->script->getValue();        $data->finished = false;        $data->statusId = (new PlannedSchedulerStatus())->id;        $data->save();*/    }}