<?phpnamespace Nemundo\App\Scheduler\Com\ListBox;use Nemundo\Admin\Com\ListBox\AdminListBox;use Nemundo\App\Scheduler\Data\SchedulerStatus\SchedulerStatusReader;class SchedulerStatusListBox extends AdminListBox{    public function getContent()    {        $this->label = 'Status';        $reader = new SchedulerStatusReader();        foreach ($reader->getData() as $schedulerStatusRow) {            $this->addItem($schedulerStatusRow->id, $schedulerStatusRow->schedulerStatus);        }        return parent::getContent();    }}