<?phpnamespace Nemundo\App\Scheduler\Page;use Nemundo\Admin\Com\Form\AdminSearchForm;use Nemundo\Admin\Com\ListBox\AdminCheckBox;use Nemundo\App\Application\Com\ListBox\ApplicationListBox;use Nemundo\App\Scheduler\Com\Table\SchedulerTable;use Nemundo\App\Scheduler\Template\SchedulerTemplate;class SchedulerPage extends SchedulerTemplate{    public function getContent()    {        $form = new AdminSearchForm($this);        $application = new ApplicationListBox($form);        $application->submitOnChange = true;        $application->value = $application->getValue();        $active = new AdminCheckBox($form);        $active->submitOnChange = true;        $active->searchMode = true;        $active->label = 'Active';        $table = new SchedulerTable($this);        $table->isActive = $active->getValue();        if ($application->hasValue()) {            $table->filterByApplicationId($application->getValue());        }        return parent::getContent();    }}