<?phpnamespace Nemundo\App\Scheduler\Page;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Admin\Com\Title\AdminTitle;use Nemundo\App\Scheduler\Reader\SchedulerDataReader;use Nemundo\App\Scheduler\Site\SchedulerStatusSite;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Com\Template\AbstractTemplateDocument;class SchedulerStatusPage extends AbstractTemplateDocument{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        $title = new AdminTitle($layout);        $title->content=SchedulerStatusSite::$site->title;        $table = new AdminTable($layout);        $schedulerReader = new SchedulerDataReader();        $header = new TableHeader($table);        $header->addText('Script');        $header->addText('Description');        $header->addText('Last Run');        $schedulerReader->filter->andEqual($schedulerReader->model->active, true);        foreach ($schedulerReader->getData() as $schedulerRow) {            $row = new AdminTableRow($table);            $row->addText($schedulerRow->script->scriptName);            $row->addText($schedulerRow->script->description);            //$schedulerRow->sc        }        return parent::getContent();    }}