<?phpnamespace Nemundo\App\Scheduler\Site\Job;use Nemundo\Admin\Site\AbstractClearSite;use Nemundo\App\Scheduler\Data\Job\JobDelete;use Nemundo\Core\Http\Url\UrlReferer;class JobCleanSite extends AbstractClearSite{    /**     * @var JobCleanSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Delete Job';        $this->url = 'delete-job';        JobCleanSite::$site = $this;    }    public function loadContent()    {        (new JobDelete())->delete();        (new UrlReferer())->redirect();    }}