<?phpnamespace Nemundo\App\Scheduler\Site\Job;use Nemundo\Admin\Site\AbstractAdminIconSite;use Nemundo\App\Scheduler\Builder\JobBuilder;use Nemundo\App\Script\Parameter\ScriptParameter;use Nemundo\App\Script\Site\ScriptRunSite;use Nemundo\Core\Http\Url\UrlReferer;class JobRunSite extends AbstractAdminIconSite{    /**     * @var ScriptRunSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Job Run';        $this->url = 'job-run';        $this->iconName = 'play';        JobRunSite::$site = $this;    }    public function loadContent()    {        $scriptId = (new ScriptParameter())->getValue();        (new JobBuilder())->buildJob($scriptId);        (new UrlReferer())->redirect();    }}