<?phpnamespace Nemundo\App\Scheduler\Site;use Nemundo\App\Scheduler\Page\SchedulerPage;use Nemundo\App\Scheduler\Site\Job\JobLogSite;use Nemundo\App\Scheduler\Site\Job\JobSite;use Nemundo\Web\Site\AbstractSite;class SchedulerSite extends AbstractSite{    /**     * @var SchedulerSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Scheduler';        $this->url = 'scheduler';        new SchedulerEditSite($this);        new LogSite($this);        new SchedulerActiveSite($this);        new SchedulerInactiveSite($this);        new SchedulerItemLogSite($this);        new RunningSite($this);        new SchedulerRunSite($this);        new JobSite($this);        new JobLogSite($this);        SchedulerSite::$site = $this;    }    public function loadContent()    {        (new SchedulerPage())->render();    }}