<?phpnamespace Nemundo\App\Script\Page;use Nemundo\Admin\Com\Form\AdminSearchForm;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\App\Application\Com\ListBox\ApplicationListBox;use Nemundo\App\Script\Com\Table\ScriptTable;use Nemundo\App\Script\Template\ScriptTemplate;use Nemundo\Com\FormBuilder\SearchForm;class ScriptPage extends ScriptTemplate{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        $search = new AdminSearchForm($layout);        $applicationListBox = new ApplicationListBox($search);        $applicationListBox->submitOnChange = true;        $applicationListBox->searchMode = true;        $table = new ScriptTable($layout);        if ($applicationListBox->hasValue()) {            $table->filterByApplicationId($applicationListBox->getValue());        }        return parent::getContent();    }}