<?phpnamespace Nemundo\App\Script\Site;use Nemundo\Admin\Site\AbstractAdminIconSite;use Nemundo\App\Script\Data\Script\ScriptReader;use Nemundo\App\Script\Parameter\ScriptParameter;use Nemundo\Core\Debug\Debug;class ScriptRunSite extends AbstractAdminIconSite{    /**     * @var ScriptRunSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Run';        $this->url = 'run';        $this->iconName = 'play';        ScriptRunSite::$site = $this;    }    public function loadContent()    {        $scriptRow = (new ScriptReader())->getRowById((new ScriptParameter())->getValue());        (new Debug())->write($scriptRow->scriptName);        $script = $scriptRow->getScript();        $script->run();    }}