<?phpnamespace Nemundo\App\Script\Type;use Nemundo\Core\Console\ConsoleInput;use Nemundo\Core\Debug\Debug;abstract class AbstractMenuConsoleScript extends AbstractConsoleScript{    protected $menuList=[];    protected $menuFunction=[];    protected function addMenu($menu,$function) {        $this->menuList[]=$menu;        $this->menuFunction[]=$function;        return $this;    }    public function run()    {        $number=1;        foreach ($this->menuList as $menu) {            (new Debug())->write($number.') '.$menu);            $number++;        }        $console = new ConsoleInput();        $console->message = 'Choice';        $console->defaultValue = '1';        $menu = $console->getValue();        $this->menuFunction[$menu-1]();        // TODO: Implement run() method.    }}