<?phpnamespace Nemundo\App\Ssh;use Nemundo\Core\Debug\Debug;abstract class AbstractSshCommand extends AbstractSsh{    /**     * @var bool     */    public $showOutput=false;    private $commandList = [];    //abstract protected function loadCommand();    protected function addCommand($command)    {        $this->commandList[] = $command;        return $this;    }    protected function runCommand()  //$command)    {        // to do:        // remove empty command line        if (!$this->connect()) {            return;        }        $sshCommand = null;        $newCommand = join(' && ', $this->commandList);        /*exec($newCommand, $output);        if (is_array($command)) {            $sshCommand = implode(';', $command);        } else {            $sshCommand = $command;        }*/        (new Debug())->write($newCommand);        $returnValue = $this->ssh->exec($newCommand);        $this->commandList = [];        return $returnValue;    }}