<?phpnamespace Nemundo\App\System\Com\Table;use Nemundo\Admin\Com\Table\AdminLabelValueTable;use Nemundo\App\Linux\Cmd\DistributionVersionCmd;use Nemundo\Core\Local\LocalCommand;use Nemundo\Core\System\PhpConfig;use Nemundo\Core\Type\DateTime\DateTime;class SystemTable extends AdminLabelValueTable{    public function getContent()    {        $this->addLabelValue('Sytem Date/Time', (new DateTime())->setNow()->getIsoDateTime());        $this->addLabelValue('Timezone', (new PhpConfig())->getTimezone());        $this->addLabelValue('IP',$_SERVER['SERVER_ADDR']);        $this->addLabelValue('Distribution', (new DistributionVersionCmd())->getVersion());        /*$cmd=new LocalCommand();        $cmd->addCommand((new DistributionVersionCmd())->getCmd());        $output = $cmd->runCommand();*/        return parent::getContent();    }}