<?phpnamespace Nemundo\App\System\Page;use Nemundo\Admin\Com\Card\AdminCard;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Com\Table\AdminLabelValueTable;use Nemundo\App\System\Com\Table\ApacheModuleTable;use Nemundo\App\System\Com\Table\MySqlSystemTable;use Nemundo\App\System\Com\Table\PhpSystemTable;use Nemundo\App\System\Com\Table\SystemTable;use Nemundo\App\System\Widget\ProjectWidget;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Db\DbConfig;use Nemundo\Db\Provider\MySql\Connection\MySqlConnection;use Nemundo\Db\Provider\SqLite\Connection\SqLiteConnection;class SystemPage extends AbstractTemplateDocument{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        $card = new AdminCard($layout);        $card->cardTitle = 'System';        new SystemTable($card);        $card = new AdminCard($layout);        $card->cardTitle = 'PHP';        new PhpSystemTable($card);        $card = new AdminCard($layout);        $card->cardTitle = 'Apache';        new ApacheModuleTable($card);        if (DbConfig::$defaultConnection->isObjectOfClass(MySqlConnection::class)) {            $card = new AdminCard($layout);            $card->cardTitle = 'MySql';            new MySqlSystemTable($card);        }        if (DbConfig::$defaultConnection->isObjectOfClass(SqLiteConnection::class)) {            $card = new AdminCard($layout);            $card->cardTitle = 'SqLite';            $table = new AdminLabelValueTable($card);            $table->addLabelValue('Filename', DbConfig::$defaultConnection->filename);        }        new ProjectWidget($layout);        return parent::getContent();    }}