<?phpnamespace Nemundo\App\UserAction\Com\Card;use Nemundo\Admin\Com\Card\AdminCard;use Nemundo\Admin\Com\Hr\AdminHr;use Nemundo\Admin\Com\Hyperlink\AdminSiteHyperlink;use Nemundo\App\UserAction\Com\Form\LoginForm;use Nemundo\App\UserAction\Site\PasswordRequestSite;use Nemundo\App\UserAction\Site\UserRegistrationSite;use Nemundo\Html\Block\Div;use Nemundo\Html\Block\Hr;use Nemundo\User\Session\UserSession;use Nemundo\Web\Site\AbstractSite;class LoginCard extends AdminCard{    /**     * @var bool     */    public $showForgotHyperlink = false;    /**     * @var bool     */    public $showRegisterHyperlink = false;    /**     * @var AbstractSite     */    public $redirectSite;    public function getContent()    {        if ((new UserSession())->isUserLogged()) {            $this->visible = false;        }        $this->cardTitle = 'Login';        $form = new LoginForm($this);        $form->autofocus = false;        $form->redirectSite = $this->redirectSite;        if ($this->showForgotHyperlink) {            (new AdminHr($this));            $div = new Div($this);            $link = new AdminSiteHyperlink($div);            $link->site = PasswordRequestSite::$site;        }        if ($this->showRegisterHyperlink) {            $div = new Div($this);            $link = new AdminSiteHyperlink($div);            $link->site = UserRegistrationSite::$site;        }        return parent::getContent();    }}