<?phpnamespace Nemundo\App\UserAction\Com\Form;use Nemundo\Admin\Com\Form\AbstractAdminForm;use Nemundo\Admin\Com\ListBox\AdminPasswordBox;use Nemundo\Package\Bootstrap\FormElement\BootstrapPasswordTextBox;use Nemundo\User\Builder\UserBuilder;use Nemundo\User\Login\Session\LoginNameSession;use Nemundo\User\Login\UserLogin;use Nemundo\User\Password\PasswordChange;use Nemundo\User\Session\UserSession;class PasswordChangeForm extends AbstractAdminForm{    /**     * @var AdminPasswordBox     */    private $password;    public function getContent()    {        $this->password = new AdminPasswordBox($this);        $this->password->label = 'Neues Passwort';        $this->password->name = 'password';        $this->password->validation = true;        $this->password->autofocus = true;        $this->submitButton->label = 'Passwort Ã¤ndern';        return parent::getContent();    }    protected function onSubmit()    {        $login = (new UserSession())->login;  // us (new LoginNameSession())->getValue();        $password = $this->password->getValue();        /*$userType = new UserBuilder((new UserSession())->userId);        $userType->changePassword($password);*/        (new PasswordChange((new UserSession())->userId))->changePassword($password);        $userLogin = new UserLogin();        $userLogin->login = $login;        $userLogin->password = $password;        $userLogin->loginUser();    }}