<?phpnamespace Nemundo\App\UserAction\Com\Form;use Nemundo\Admin\Com\Button\AdminSubmitButton;use Nemundo\Admin\Com\ListBox\AdminPasswordBox;use Nemundo\App\UserAction\Site\PasswordRequestSite;use Nemundo\App\UserAction\Site\UserRegistrationSite;use Nemundo\Com\FormBuilder\AbstractFormBuilder;use Nemundo\Com\Html\Hyperlink\SiteHyperlink;use Nemundo\Core\Language\LanguageCode;use Nemundo\Html\Block\Hr;use Nemundo\Package\Bootstrap\FormElement\BootstrapCheckBox;use Nemundo\Package\Bootstrap\FormElement\BootstrapPasswordTextBox;use Nemundo\Package\Bootstrap\FormElement\BootstrapTextBox;use Nemundo\User\Login\UserLogin;class PasswordLoginForm extends AbstractFormBuilder{    public $login;    /**     * @var  BootstrapPasswordTextBox     */    private $password;    /**     * @var BootstrapCheckBox     */    private $saveLogin;    public function getContent()    {        $this->password = new AdminPasswordBox($this);        $this->password->label[LanguageCode::EN] = 'Password';        $this->password->label[LanguageCode::DE] = 'Passwort';        $this->password->name = 'password';        $this->password->errorMessage = 'UngÃ¼ltiges Passwort';        $submitButton = new AdminSubmitButton($this);        $submitButton->label = 'Login';        return parent::getContent();    }    protected function onValidate()    {        $returnValue = null;        $login = new UserLogin();        $login->login = $this->login;        $login->password = $this->password->getValue();        $login->saveCookiePassword = true;        if ($login->loginUser()) {            $returnValue = true;        } else {            $this->password->showErrorMessage = true;            $returnValue = false;        }        return $returnValue;    }    protected function onSubmit()    {    }}