<?phpnamespace Nemundo\App\UserAction\Com\Form;use Nemundo\Admin\Com\Form\AbstractAdminForm;use Nemundo\Com\FormBuilder\AbstractFormBuilder;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\User\Builder\UserBuilder;use Nemundo\User\Com\ListBox\UserListBox;use Nemundo\User\Data\User\UserReader;use Nemundo\User\Login\UserLogin;use Nemundo\User\Session\UserSession;class UserChangeForm extends AbstractAdminForm{    /**     * @var UserListBox     */    private $userListBox;    public function getContent()    {        $this->userListBox = new UserListBox($this);        $this->userListBox->submitOnChange = true;        $this->userListBox->emptyValueAsDefault = false;        $this->userListBox->value = (new UserSession())->userId;        $this->hideSubmitButton();        return parent::getContent();    }    protected function onSubmit()    {        $userRow = (new UserReader())->getRowById($this->userListBox->getValue());        $userLogin = new UserLogin();        $userLogin->login = $userRow->login;        $userLogin->passwordVerification = false;        $userLogin->loginUser();        (new UrlReferer())->redirect();    }}