<?phpnamespace Nemundo\App\UserAction\Page;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\App\UserAction\UserActionConfig;use Nemundo\App\UserAction\Widget\LoginWidget;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Web\Site\BaseUrlSite;class LoginPage extends AbstractTemplateDocument{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        $widget = new LoginWidget($layout);        $widget->showForgotHyperlink = UserActionConfig::$showForgotHyperlink;        $widget->showRegisterHyperlink = UserActionConfig::$showRegisterHyperlink;        $widget->redirectSite = new BaseUrlSite();        return parent::getContent();    }}