<?phpnamespace Nemundo\App\UserAction\Page;use Nemundo\Admin\Com\Title\AdminTitle;use Nemundo\App\UserAction\Com\Form\PasswordChangeForm;use Nemundo\App\UserAction\Site\PasswordChangeSite;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Core\Language\LanguageCode;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\User\Parameter\PasswordChangeParameter;class PasswordChangePage extends AbstractTemplateDocument{    public function getContent()    {        $title = new AdminTitle($this);        $title->content[LanguageCode::EN] = 'Change your password';        $title->content[LanguageCode::DE] = 'Ã„ndern sie ihr Passwort';        $passwordChangeParameter = new PasswordChangeParameter();        if (!$passwordChangeParameter->exists()) {            $form = new PasswordChangeForm($this);            $form->redirectSite = PasswordChangeSite::$site;            $form->redirectSite->addParameter($passwordChangeParameter);        } else {            $p = new Paragraph($this);            $p->content = 'Passwort wurde geÃ¤ndert.';        }        return parent::getContent();    }}