<?phpnamespace Nemundo\App\UserAction\Page;use Nemundo\App\UserAction\Com\Form\PasswordRequestForm;use Nemundo\App\UserAction\Site\PasswordRequestSite;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Web\Parameter\UrlParameter;class PasswordRequestPage extends AbstractTemplateDocument{    public function getContent()    {        $parameter = new UrlParameter();        $parameter->parameterName = 'pwd';        if ($parameter->notExists()) {            $form = new PasswordRequestForm($this);            $form->redirectSite = PasswordRequestSite::$site;            $parameter = new UrlParameter();            $parameter->parameterName = 'pwd';            $parameter->value = 1;            $form->redirectSite->addParameter($parameter);        } else {            $p = new Paragraph($this);            $p->content = 'Link zum Ã„ndern des Passwort wurde geschickt.';        }        return parent::getContent();    }}