<?phpnamespace Nemundo\App\UserAction\Page;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Com\Title\AdminTitle;use Nemundo\App\UserAction\Com\Form\UserActivationForm;use Nemundo\App\UserAction\Site\UserActivationSite;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Web\Site\BaseUrlSite;class UserActivationPage extends AbstractTemplateDocument{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        $title = new AdminTitle($layout);        $title->content = UserActivationSite::$site->title;        $form = new UserActivationForm($layout);        $form->redirectSite = (new BaseUrlSite());        return parent::getContent();    }}