<?phpnamespace Nemundo\App\UserAction\Site;use Nemundo\App\UserAction\Page\PasswordRequestPage;use Nemundo\Core\Language\LanguageCode;use Nemundo\Web\Site\AbstractSite;class PasswordRequestSite extends AbstractSite{    /**     * @var PasswordRequestSite     */    public static $site;    protected function loadSite()    {        $this->title[LanguageCode::EN] = 'Request Password';        $this->title[LanguageCode::DE] = 'Passwort vergessen';        $this->url = 'request-password';        $this->menuActive = false;        PasswordRequestSite::$site = $this;    }    public function loadContent()    {        (new PasswordRequestPage())->render();    }}