<?phpnamespace Nemundo\App\UserAdmin\Page;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\App\UserAdmin\Com\Form\UserEditForm;use Nemundo\App\UserAdmin\Com\Form\UserForm;use Nemundo\App\UserAdmin\Com\Tab\UserAdminTab;use Nemundo\App\UserAdmin\Site\UserAdminSite;use Nemundo\App\UserAdmin\Template\UserAdminTemplate;use Nemundo\User\Parameter\UserParameter;class UserEditPage extends UserAdminTemplate{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        new UserAdminTab($layout);        $form = new UserEditForm($layout);  // new UserForm($layout);        $form->userId = (new UserParameter())->getValue();        $form->redirectSite = UserAdminSite::$site;        return parent::getContent();    }}