<?phpnamespace Nemundo\App\UserAdmin\Site;use Nemundo\Admin\Site\AbstractDeleteIconSite;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\User\Builder\UserBuilder;use Nemundo\User\Parameter\UserParameter;class UserDeleteSite extends AbstractDeleteIconSite{    /**     * @var UserDeleteSite     */    public static $site;    protected function loadSite()    {        $this->url = 'delete';        //$this->icon = new DeleteIcon();        $this->menuActive = false;        UserDeleteSite::$site = $this;    }    public function loadContent()    {        $user = new UserBuilder((new UserParameter())->getValue());        $user->deleteUser();        (new UrlReferer())->redirect();    }}