<?phpnamespace Nemundo\App\WebService\ClassDesigner;use Nemundo\App\ClassDesigner\Builder\AbstractClassBuilder;use Nemundo\App\WebService\Service\AbstractWebService;use Nemundo\Core\Type\Text\Text;use Nemundo\Dev\Code\PhpClass;use Nemundo\Dev\Code\PhpFunction;use Nemundo\Dev\Code\PhpVisibility;class ServiceRequestClassBuilder extends AbstractClassBuilder{    public function buildClass()    {        $namespace = $this->namespace . '\Service';        $typeClassName = $this->className . 'Service';        $phpClass = new PhpClass();        $phpClass->project = $this->project;        $phpClass->className = $typeClassName;        $phpClass->extendsFromClass = 'AbstractWebService';        $phpClass->namespace = $namespace;        $phpClass->addUseClass(AbstractWebService::class);        $function = new PhpFunction($phpClass);        $function->visibility = PhpVisibility::ProtectedVariable;        $function->functionName = 'loadService()';        $function->add('$this->serviceName=\'' . (new Text($this->className))->changeToLowercase()->getValue() . '\';');        $function = new PhpFunction($phpClass);        $function->functionName = 'onRequest()';        $phpClass->saveFile();    }}