<?phpnamespace Nemundo\App\WebService\Com;use Nemundo\Admin\Com\Copy\CopyTextBox;use Nemundo\Admin\Com\Widget\AdminWidget;use Nemundo\App\WebService\Request\AbstractWebService;use Nemundo\App\WebService\Request\AbstractWebServicePostRequest;use Nemundo\Html\Container\AbstractHtmlContainer;use Nemundo\Package\Bootstrap\FormElement\BootstrapTextBox;use Nemundo\Web\Site\AbstractSite;class WebServiceInformation extends AbstractHtmlContainer{    /**     * @var AbstractSite     */    public $webServiceSite;    /**     * @var AbstractWebService     */    public $webService;    public function getContent()    {        $widget=new AdminWidget($this);        $widget->widgetTitle= $this->webService->webService;        foreach ($this->webService->getParameterList() as $parameter) {            $input = new BootstrapTextBox($widget);            $input->label=$parameter->parameterName;        }        $txt = new CopyTextBox($widget);        $txt->label = $this->webService->webService;  // 'Web Service';        $txt->value = $this->webService->getUrl(); // getUrlWithDomain();        return parent::getContent();    }}