<?phpnamespace Nemundo\App\WebService\Page;use Nemundo\Admin\Com\Form\AdminSearchForm;use Nemundo\Admin\Com\Layout\AdminFlexboxLayout;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\App\Application\Com\ListBox\ApplicationListBox;use Nemundo\App\WebService\Com\Tab\WebServiceTab;use Nemundo\App\WebService\Data\ServiceRequest\ServiceRequestReader;use Nemundo\App\WebService\Site\ServiceRequestSite;use Nemundo\Com\Template\AbstractTemplateDocument;use Nemundo\Web\Parameter\UrlParameter;class WebServicePage extends AbstractTemplateDocument{    public function getContent()    {        $layout = new AdminFlexboxLayout($this);        new WebServiceTab($layout);        $row = new AdminSearchForm($layout);        $application = new ApplicationListBox($row);        $application->submitOnChange = true;        $application->searchMode = true;        $table = new AdminTable($layout);        $reader = new ServiceRequestReader();        $reader->model->loadApplication();        $reader->addOrder($reader->model->uniqueName);        if ($application->hasValue()) {            $reader->filter->andEqual($reader->model->applicationId, $application->getValue());        }        $header = new AdminTableHeader($table);        $header->addText($reader->model->uniqueName->label);        $header->addText($reader->model->phpClass->label);        $header->addText($reader->model->application->label);        $header->addEmpty();        foreach ($reader->getData() as $serviceRequestRow) {            $row = new AdminTableRow($table);            $row->addText($serviceRequestRow->uniqueName);            $row->addText($serviceRequestRow->phpClass);            $row->addText($serviceRequestRow->application->application);            $parameter = new UrlParameter($serviceRequestRow->uniqueName);            $parameter->parameterName = 'service';            $site = clone(ServiceRequestSite::$site);            $site->addParameter($parameter);            $row->addSite($site);        }        return parent::getContent();    }}