<?phpnamespace Nemundo\App\WebService\Request;use Nemundo\App\WebService\Site\AbstractPostRequestSite;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Base\AbstractBaseClass;use Nemundo\Web\Parameter\AbstractUrlParameter;use Nemundo\Web\Site\AbstractSite;abstract class AbstractWebService extends AbstractBaseClass{    public $webServiceId;    public $webService;    /**     * @var AbstractSite     */    public $site;    public $url;    /**     * @var AbstractUrlParameter[]     */    private $parameterList=[];    /**     * @var AbstractPostRequestSite     */    //public $postRequestSite;    abstract protected function loadWebService();    public function __construct()    {        $this->loadWebService();    }    protected function addParameter(AbstractUrlParameter $parameter) {        $this->parameterList[]=$parameter;        return $this;    }    public function getParameterList() {        return $this->parameterList;    }    public function getUrl() {        $site = clone($this->site);        foreach ($this->parameterList as $parameter) {            $site->addParameter($parameter);        }        $url = $site->getUrlWithDomain();        return $url;    }    public function onCall() {    }}