<?phpnamespace Nemundo\App\WebService\Site;use Nemundo\App\WebService\Page\WebServicePage;use Nemundo\Web\Site\AbstractSite;class WebServiceSite extends AbstractSite{    /**     * @var WebServiceSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Web Service';        $this->url = 'web-service';        WebServiceSite::$site = $this;        new KeySite($this);    }    public function loadContent()    {        (new WebServicePage())->render();    }}