<?phpnamespace Nemundo\Com\Chart\Data;use Nemundo\Com\Chart\AbstractChart;class PieChartData extends AbstractChartData{    public $legend;    public function __construct(AbstractChart $chart)    {        parent::__construct($chart);        $this->chartType = ChartType::PIE;    }    public function addLegendValue($legend,$value)    {        if ($value === null) {            $value = 'null';        }        $text = '{ value: '.$value.', name: "'.$legend.'" }';        $this->valueList->addValue($text);        return $this;    }}