<?phpnamespace Nemundo\Com\FormBuilder\Item;use Nemundo\Core\Http\Request\Post\PostRequest;use Nemundo\Core\Log\LogMessage;// AbstractCheckBoxGroupclass AbstractRadioGroup extends AbstractFormBuilderItem{    /**     * @var string     */    public $label;    /**     * @var string     */    public $prefix = 'group_';    /**     * @var bool     */    public $submitOnChange = false;    /**     * @var     */    //protected $groupLabel;    /**     * @var AbstractCheckBox[]     */    protected $radioButtonList = [];    /**     * @var ListItem[]     */    protected $itemList = [];    protected function prepareHtml()    {    }    // addCheckBox    function addItem($id, $label)    {        $item = new ListItem();        $item->label = $label;        $item->value = $id;        $this->itemList[$id] = $item;        return $this;    }    public function getValue()    {        $value = $this->getInternalValue();        return $value;    }    public function setValue($id, $value = true)    {        if (isset($this->itemList[$id])) {            $this->itemList[$id]->checked = $value;  // true;        } else {            (new LogMessage())->writeError('CheckBox ' . $id . ' not exists.');        }        return $this;    }    public function getValueList()    {        $valueList = [];        foreach ($this->itemList as $listItem) {            $request = new PostRequest($this->prefix . $listItem->value);            if ($request->existsRequest()) {                $valueList[] = $listItem->value;            }        }        return $valueList;    }}