<?phpnamespace Nemundo\Com\Html\Hyperlink;use Nemundo\Html\Hyperlink\AbstractHyperlink;class UrlHyperlink extends AbstractHyperlink{    use HyperlinkTrait;    /**     * @var string     */    public $url;    /**     * @var string     */    public $title;    public function getContent()    {        $this->checkContainerVisibility();        $this->href = $this->url;        $this->loadHyperlink();        if ($this->content==null) {            $this->content = $this->url;        }        return parent::getContent();    }}