<?phpnamespace Nemundo\Com\Package\Setup;use Nemundo\Com\Package\Type\AbstractPackage;use Nemundo\Core\Base\AbstractBaseClass;use Nemundo\Core\Debug\Debug;use Nemundo\Core\File\DirectoryCopy;use Nemundo\Core\Path\Path;use Nemundo\FrameworkProject;use Nemundo\Web\WebConfig;class PackageSetup extends AbstractBaseClass{    public $destinationPath;    public function __construct()    {        $this->destinationPath = WebConfig::$webPath;    }    public function addPackage(AbstractPackage $package)    {        if ($package->project == null) {            $package->project = new FrameworkProject();            (new Debug())->write('no project: ' . $package->getClassName());        }        $sourcePath = (new Path())            ->addPath($package->project->path)            ->addPath('..')            ->addPath('package')            ->addPath($package->packageName)            ->addPath($package->getPrefix())            ->getPath();        $destinationPath = (new Path($this->destinationPath))            ->addPath('package')            ->addPath($package->getPrefix())            ->addPath($package->packageName)            ->getPath();        $copy = new DirectoryCopy();        $copy->overwriteExistingFile = true;        $copy->sourcePath = $sourcePath;        $copy->destinationPath = $destinationPath;        $copy->copyDirectory();        return $this;    }}