<?phpnamespace Nemundo\Com\Package\Type;use Nemundo\Core\Base\AbstractBaseClass;use Nemundo\Project\AbstractProject;use Nemundo\Web\WebConfig;abstract class AbstractPackage extends AbstractBaseClass{    /**     * @var string     */    public $packageName;    /**     * @var AbstractProject     */    public $project;    private $jsList = [];    private $cssList = [];    //private $fontList = [];    protected $prefix;    abstract protected function loadPackage();    public function __construct()    {        $this->loadPackage();    }    public function getPrefix() {        return $this->prefix;    }    protected function getBaseUrl()    {        $baseUrl = '/package/'.$this->getPrefix().'/' . $this->packageName . '/';        return $baseUrl;    }    protected function addJs($filename)    {        $this->jsList[] = $filename;        //$this->jsList[] =  '/package/'.$this->getPrefix().'/'. $this->packageName . '/' . $filename;        return $this;    }    protected function addCss($filename)    {        //$this->cssList[] = '/package/'.$this->getPrefix().'/'. $this->packageName . '/' . $filename;        $this->cssList[] = $filename;        return $this;    }    public function getJsList()    {        $list = [];        foreach ($this->jsList as $jsUrl) {            $list[] = $this->getBaseUrl() . $jsUrl;        }        return $list;    }    public function getCss()    {        $list = [];        foreach ($this->cssList as $jsUrl) {            $list[] = $this->getBaseUrl() . $jsUrl;        }        return $list;        //return $this->cssList;    }}