<?phpnamespace Nemundo\Com\Template;use Nemundo\Dev\App\Factory\DefaultTemplateFactory;use Nemundo\Html\Document\AbstractDocument;class AbstractTemplateDocument extends AbstractDocument{    public function render()    {        $page = (new DefaultTemplateFactory())->getDefaultTemplate();        $page->statusCode = $this->statusCode;        $page->addContainer($this);        $page->render();    }}