<?phpnamespace Nemundo\Com\Video\YouTube;use Nemundo\Com\Video\AbstractVideoPlayer;use Nemundo\Core\Http\Url\UrlBuilder;use Nemundo\Html\Iframe\Iframe;// https://developers.google.com/youtube/iframe_api_reference// Packageclass YouTubePlayer extends AbstractVideoPlayer{    public $start;    public function getContent()    {        //https://martinahonecker.com/youtube-videos-responsive-einbinden/        //$this->checkProperty('videoId');        //$url = 'https://www.youtube.com/embed/' . $this->videoId;        $urlBuilder = new UrlBuilder('https://www.youtube.com/embed/' . $this->videoId);        if ($this->start !== null) {            $urlBuilder->addRequestValue('start', $this->start);        }        $iframe = new Iframe($this);        //$iframe->addCssClass('admin-embedded-video');        //$iframe->addCssClass('video');        $iframe->width = $this->width;        $iframe->height = $this->height;        $iframe->src = $urlBuilder->getUrl();        $iframe->addAttribute('allow', 'accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture');        $iframe->addAttributeWithoutValue('allowfullscreen');        return parent::getContent();    }}