<?phpnamespace Nemundo\Com\Video\YouTube;use Nemundo\Com\Video\AbstractVideoPlayer;use Nemundo\Core\Http\Url\UrlBuilder;use Nemundo\Html\Iframe\Iframe;class YouTubePlayer extends AbstractVideoPlayer{    public $start;    public function getContent()    {        $urlBuilder = new UrlBuilder('https://www.youtube.com/embed/' . $this->videoId);        if ($this->start !== null) {            $urlBuilder->addRequestValue('start', $this->start);        }        $iframe = new Iframe($this);        $iframe->width = $this->width;        $iframe->height = $this->height;        $iframe->src = $urlBuilder->getUrl();        $iframe->addAttribute('allow', 'accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture');        $iframe->addAttributeWithoutValue('allowfullscreen');        return parent::getContent();    }}