<?phpnamespace Nemundo\Crawler\HtmlParser;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Type\Text\Text;class HyperlinkItem extends AbstractBase{    public $title;    public $url;    public $fullUrl;    public $domain;    /**     * @var bool     */    public $external = false;    public function getUrlDeep()    {        $urlDeep = substr_count($this->fullUrl, '/') - 2; // 2        return $urlDeep;    }    public function isUrl()    {        $value = true;        if ($this->isJavascript() || $this->isPhone() || $this->isEmail()) {            $value = false;        }        return $value;    }    public function isJavascript()    {        return $this->checkPrefix('javascript:');    }    public function isPhone()    {        return $this->checkPrefix('tel:');    }    public function isEmail()    {        return $this->checkPrefix('mailto:');    }    private function checkPrefix($prefix)    {        $value = false;        if ((new Text($this->url))->containsLeft($prefix)) {            $value = true;        }        return $value;    }}