<?phpnamespace Nemundo\Dev\Code;use Nemundo\Core\Base\Document\AbstractDocument;use Nemundo\Core\TextFile\Writer\TextFileWriter;class PhpFile extends AbstractDocument{    /**     * @var bool     */    public $overwriteExistingFile = false;    /**     * @var array     */    private $lineList = [];    public function add($line)    {        $this->lineList[] = $line;    }    protected function onWrite($fullFilename)    {        $textFile = new TextFileWriter($fullFilename);        $textFile->overwriteExistingFile = $this->overwriteExistingFile;        $textFile->addLine('<?php');        foreach ($this->lineList as $line) {            $textFile->addLine($line);        }        $textFile->writeFile();    }    protected function onRender()    {    }}