<?phpnamespace Nemundo\Dev\Deployment;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\File\DirectoryCopy;use Nemundo\Core\Path\Path;use Nemundo\Core\Repository\AbstractRepository;use Nemundo\Core\Type\Text\Text;class ProjectDeployment extends AbstractBase{    /**     * @var Path     */    public $path;    public function copyProject(AbstractRepository $project)    {        $namespace = (new Text($project->namespace))->replace('\\', DIRECTORY_SEPARATOR)->getValue();        $copy = new DirectoryCopy();        $copy->overwriteExistingFile = true;        $copy->sourcePath = $project->path;        $copy->destinationPath = (clone($this->path))            ->addPath('src')            ->addPath($namespace)            ->createPath()            ->getPath();        $copy->copyDirectory();        return $this;    }}