<?phpnamespace Nemundo\Dev\Install;use Nemundo\Com\Package\Setup\PackageSetup;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\File\FileCopy;use Nemundo\Core\Path\Path;use Nemundo\Core\Random\RandomNumber;use Nemundo\Core\TextFile\Writer\TextFileWriter;use Nemundo\FrameworkProject;use Nemundo\Package\FontAwesome\Package\FontAwesomePackage;use Nemundo\Package\Framework\CoreJsPackage;use Nemundo\Package\Framework\FrameworkCssPackage;use Nemundo\Package\Framework\FrameworkJsPackage;use Nemundo\Package\Framework\HtmlJsPackage;use Nemundo\Package\Jquery\Package\JqueryPackage;use Nemundo\Package\JqueryUi\JqueryUiPackage;use Nemundo\Project\Path\ProjectPath;class AdminPackageInstall extends AbstractBase{    /**     * @var string     */    private $projectPath;    public function install()    {        $adminPath = (new ProjectPath())            ->addPath('admin_web');        $this->projectPath = $adminPath->getPath();        $adminPath->emptyDirectory();        $this->copyAssetFile('.htaccess', '.htaccess');        $this->copyAssetFile('config.php', 'config.php');        $this->copyAssetFile('index.php', 'index.php');        $filename = (new ProjectPath())            ->addPath('admin.bat')            ->getFullFilename();        $random = new RandomNumber();        $random->minNumber = 10000;        $random->maxNumber = 50000;        $port = $random->getNumber();        $write = new TextFileWriter($filename);        $write->overwriteExistingFile = true;        $write            ->addLine('start http://localhost:' . $port . '/')            ->addLine('cd admin_web')            ->addLine('php -S localhost:' . $port);        $write->writeFile();        $setup = new PackageSetup();        $setup->destinationPath = $this->projectPath;        $setup            ->addPackage(new FontAwesomePackage())            ->addPackage(new FrameworkCssPackage())            ->addPackage(new CoreJsPackage())            ->addPackage(new HtmlJsPackage())            ->addPackage(new FrameworkJsPackage())            ->addPackage(new JqueryPackage())            ->addPackage(new JqueryUiPackage());    }    private function copyAssetFile($filenameFrom, $filenameTo)    {        $fileCopy = new FileCopy();        $fileCopy->overwriteExistingFile = true;        $fileCopy->sourceFilename = (new Path())            ->addPath((new FrameworkProject())->path)            ->addPath('..')            ->addPath('package')            ->addPath('admin')            ->addPath($filenameFrom)            ->getFilename();        $fileCopy->destinationFilename = (new Path())            ->addPath($this->projectPath)            ->addPath($filenameTo)            ->getFilename();        $fileCopy->copyFile();    }}