<?phpnamespace Nemundo\Dev\ProjectBuilder\Code;use Nemundo\App\Script\Setup\ScriptSetup;use Nemundo\Dev\Code\PhpClass;use Nemundo\Dev\Code\PhpFunction;use Nemundo\Project\Install\ProjectInstall;use Nemundo\Project\Reset\ProjectReset;use Nemundo\Project\Setup\AbstractSetup;use Nemundo\User\Script\AdminUserScript;class SetupProjectCode extends AbstractProjectCode{    public function createCode()    {        $phpClass = new PhpClass();        $phpClass->path = $this->path;        $phpClass->namespace = $this->prefixNamespace . '\\Setup';        $phpClass->className = $this->prefixNamespace . 'Setup';        $phpClass->extendsFromClass = 'AbstractSetup';        $phpClass->addUseClass(AbstractSetup::class);        $phpClass->addUseClass(ProjectInstall::class);        $phpClass->addUseClass(AdminUserScript::class);        $phpClass->addUseClass(ScriptSetup::class);        $phpClass->addUseClass(ProjectReset::class);        $function = new PhpFunction($phpClass);        $function->functionName = 'run()';        $function->add('$reset = new ProjectReset();');        $function->add('$reset->reset();');        $function->add('(new ProjectInstall())->install();');        $function->add('(new ScriptSetup())->addScript(new AdminUserScript());');        $function->add('$reset->remove();');        $phpClass->saveFile();    }}