<?phpnamespace Nemundo\Svg\Geo;use Nemundo\Core\Type\Geo\AbstractGeoCoordinate;use Nemundo\Svg\Coordinate;class GeoCoordinateXyConverter{    public $width;    public $height;    public function getXY(AbstractGeoCoordinate $geoCoordinate) {        $coordinate=new Coordinate();        $coordinate->x = ($this->width/360)*(180+$geoCoordinate->longitude);        $coordinate->y = ($this->height/180)*(90-$geoCoordinate->latitude);        return $coordinate;        /*        int x =  (int) ((MAP_WIDTH/360.0) * (180 + lon));int y =  (int) ((MAP_HEIGHT/180.0) * (90 - lat));        */    }}