<?phpnamespace Nemundo\Office\Excel\Document;use Nemundo\Core\Base\AbstractDocument;use Nemundo\Core\Http\Response\HttpResponse;use Nemundo\Core\Http\Response\ResponseType;use PhpOffice\PhpSpreadsheet\Spreadsheet;use PhpOffice\PhpSpreadsheet\Writer\Xlsx;abstract class AbstractExcelDocument extends AbstractDocument{    /**     * @var string     */    protected $filename = 'document.xlsx';    /**     * @var Spreadsheet     */    private $spreadsheet;    /**     * @var int     */    private $rowCount = 1;    /**     * @var array     */    private $rowList = [];    abstract protected function loadExcel();    function __construct()    {        $this->spreadsheet = new Spreadsheet();    }    public function addBoldRow($row)    {        $this->addRow($row, true);    }    public function addRow($row, $bold = false)    {        $this->rowList[] = $row;        $count = 1;        foreach ($row as $value) {            $this->spreadsheet->setActiveSheetIndex(0)->setCellValue([$count, $this->rowCount], $value);            if ($bold) {                $this->spreadsheet->setActiveSheetIndex(0)->getStyle([$count, $this->rowCount])->getFont()->setBold(true);            }            $count++;        }        $this->rowCount++;    }    public function addEmptyRow()    {        $this->addRow([]);    }    protected function writeFileIntern($filename)    {        $this->loadExcel();        foreach ($this->spreadsheet->getActiveSheet()->getColumnIterator() as $column) {            $this->spreadsheet->getActiveSheet()->getColumnDimension($column->getColumnIndex())->setAutoSize(true);        }        $writer = new Xlsx($this->spreadsheet);        $writer->save($filename);    }    public function writeFile()    {        $this->writeFileIntern($this->filename);    }    public function render()    {        $response = new HttpResponse();        $response->contentType = ResponseType::EXCEL;        $response->attachmentFilename = $this->filename;        $response->sendResponse();        $this->writeFileIntern('php://output');    }}